package Lesson_10;import java.util.Scanner;public class IfElseStatements {    /**     * Tutorial link:     * https://www.youtube.com/watch?v=yvWnj_HfG6s&list=PL59LTecnGM1NRUyune3SxzZlYpZezK-oQ&index=10     */    public static void main(String[] args) {        Scanner s = new Scanner(System.in);        int grade;        System.out.println("Enter grade: ");        grade = s.nextInt();        if (grade >= 8 && grade <= 10) {            System.out.println("Excellent");        } else if (grade >= 3 && grade <= 7) {            System.out.println("Keep the good work");        } else if (grade > 0 && grade < 3) {            System.out.println("Bad score");        } else {            System.out.println("Not in a range");        }    }}