package Lesson_9;import java.util.Scanner;public class BooleanInJava {    /**     * Tutorial link:     * https://www.youtube.com/watch?v=CHVVEGRGiJU&list=PL59LTecnGM1NRUyune3SxzZlYpZezK-oQ&index=9     *     * Semua tipe data primitive gak bisa diisi NULL atau bentuk returnnya NULL     * Tapi ketika diubah jadi objek bisa     *     * Contoh tipe data primitive:  boolean b = null; <- gabisa     * Contoh tipe data objek:      Boolean x = null; <- bisa     */    public static void main(String[] args) {        Scanner s = new Scanner(System.in);        int doorCount = 0;        boolean chance = true;        String jawab;        if (chance) {            System.out.print("Door 1! - Easy\n1 + 1 = ");            jawab = s.nextLine();            if (jawab.equalsIgnoreCase("2")) {                System.out.println("You passed the 1st door!");                doorCount++;            } else {                chance = false;            }        }        if (chance) {            System.out.print("Door 2! - Intermediate\n2 + 2 = ");            jawab = s.nextLine();            if (jawab.equalsIgnoreCase("4")) {                System.out.println("You passed the 2nd door!");                doorCount++;            } else {                chance = false;            }        }        if (chance) {            System.out.print("Door 3! - Hard\n4 + 4 = ");            jawab = s.nextLine();            if (jawab.equalsIgnoreCase("8")) {                System.out.println("You passed the 3rd door!");                doorCount++;            } else {                chance = false;            }        }        if (!chance) {            System.out.println("===================================");            System.out.println("Door count: %d".formatted(doorCount));            System.out.println("Sorry, you're fail!");            System.exit(0);        } else {            System.out.println("===================================");            System.out.println("Door count: %d".formatted(doorCount));            System.out.println("Congratulations! You passed!");            System.exit(0);        }    }}